import React, { useState, useEffect } from 'react';
import '../styles/players.css';

function Players() {
  const [players, setPlayers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const perPage = 9; // Number of players per page (three per row)

  useEffect(() => {
    fetchPlayers();
  }, [currentPage]); // Fetch players whenever currentPage changes

  const fetchPlayers = () => {
    fetch(`http://127.0.0.1:5000/players?page=${currentPage}`)
      .then(response => response.json())
      .then(data => {
        console.log('Fetched players:', data); // Debugging to see fetched data structure
        setPlayers(data);
      })
      .catch(error => console.error('Error fetching players:', error));
  };

  // Function to handle page change
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Total number of pages
  const totalPages = Math.ceil(players.length / perPage);

  // Centered pagination style
  const paginationStyle = {
    display: 'flex',
    justifyContent: 'center',
    marginTop: '20px', // Adjust margin as needed
  };

  return (
    <div className="players-container">
      <header className="header">
        <h1>Players Information</h1>
      </header>
      <div className="players-grid">
        {players.map(player => (
          <div className="player-card card" key={player.name}>
            <img
              src={player.photo}
              className="card-img-top img-fluid"
              alt={player.name}
            />
            <div className="card-body">
              <h3 className="card-title">{player.name}</h3>
              <p className="card-text">Age: {player.age}</p>
              <p className="card-text">Number: {player.number}</p>
              <p className="card-text">Position: {player.position}</p>
              <p className="card-text">Club: {player.club}</p>
            </div>
          </div>
        ))}
      </div>
      {/* Pagination */}
      <div style={paginationStyle}>
        <nav aria-label="Page navigation example">
          <ul className="pagination">
            <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
              <button className="page-link" onClick={() => handlePageChange(currentPage - 1)}>Previous</button>
            </li>
            <li className="page-item">
              <button className="page-link" onClick={() => handlePageChange(currentPage - 3)} disabled={currentPage <= 3}>3 Back</button>
            </li>
            <li className="page-item">
              <button className="page-link" onClick={() => handlePageChange(currentPage + 3)} disabled={currentPage + 3 > totalPages}>3 Next</button>
            </li>
            <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`}>
              <button className="page-link" onClick={() => handlePageChange(totalPages)}>Last</button>
            </li>
          </ul>
        </nav>
      </div>
    </div>
  );
}

export default Players;
