import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import "../styles/teammates.css"

const Teammates = () => {
  const [players, setPlayers] = useState([]);
  const { playerName } = useParams();

  useEffect(() => {
    if (playerName) {
      const fetchPlayers = async () => {
        try {
          const response = await axios.get(`http://127.0.0.1:5000/teammates/${playerName}`);
          setPlayers(response.data);
        } catch (error) {
          console.error('Failed to fetch teammates:', error);
        }
      };
      fetchPlayers();
    }
  }, [playerName]);

  return (
    <div className="teammates-container">
      {players.map(player => (
        <div className="teammate-card" key={player.id}>
          <img src={player.photo} alt={player.name} className="teammate-photo" />
          <div className="teammate-info">
            <h4>{player.name}</h4>
            <p>{player.position}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Teammates;
