import React, { useState, useEffect } from 'react';
import '../styles/slideshow.css';

const Slideshow = ({ videos }) => {
  const [slideIndex, setSlideIndex] = useState(1);

  useEffect(() => {
    showSlides(slideIndex);
  }, [slideIndex]);

  const plusSlides = (n) => {
    setSlideIndex((prev) => (prev + n > videos.length ? 1 : prev + n < 1 ? videos.length : prev + n));
  };

  const currentSlide = (n) => {
    setSlideIndex(n);
  };

  const showSlides = (n) => {
    const slides = document.getElementsByClassName('mySlides');
    const dots = document.getElementsByClassName('dot');
    for (let i = 0; i < slides.length; i++) {
      slides[i].style.display = 'none';
    }
    for (let i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(' active', '');
    }
    slides[slideIndex - 1].style.display = 'block';
    dots[slideIndex - 1].className += ' active';
  };

  return (
    <div>
      <div className="slideshow-container">
        {videos.map((video, index) => (
          <div className="mySlides fade" key={index}>
            <div className="numbertext">{index + 1} / {videos.length}</div>
            <iframe width="100%" height="500" src={video} frameBorder="0" allowFullScreen></iframe>
            <div className="text">Video {index + 1}</div>
          </div>
        ))}
        <a className="prev" onClick={() => plusSlides(-1)}>&#10094;</a>
        <a className="next" onClick={() => plusSlides(1)}>&#10095;</a>
      </div>
      <br />
      <div style={{ textAlign: 'center' }}>
        {videos.map((video, index) => (
          <span className="dot" key={index} onClick={() => currentSlide(index + 1)}></span>
        ))}
      </div>
    </div>
  );
};

export default Slideshow;
