import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/clubs.css';

const apiUrl = 'http://localhost:5000/api/teams'; // URL of your Node.js server endpoint

function Clubs() {
  const [clubs, setClubs] = useState([]);

  useEffect(() => {
    const fetchClubs = async () => {
      try {
        const response = await axios.get(apiUrl);
        setClubs(response.data.teams);
      } catch (error) {
        console.error('Error fetching clubs:', error);
      }
    };

    fetchClubs();
  }, []);

  return (
    <div className="container">
      <h1>Clubs</h1>

      {clubs.map(club => (
        <div key={club.id} className="card mb-3 club-card">
          <div className="row g-0">
            <div className="col-md-8">
              <div className="card-body">
                <h2>{club.name}</h2>
                <p><strong>League:</strong> {club.area.name}</p>
                <p><strong>Stadium:</strong> {club.venue}</p>
                <p><strong>City/Location:</strong> {club.address}</p>
                <p><strong>Trophy Count:</strong> {club.trophies}</p>
              </div>
            </div>
            <div className="col-md-4">
              <img src={club.crestUrl} className="img-fluid rounded-end club-img" alt={club.name} />
            </div>
          </div>
        </div>
      ))}

      <footer>
        <small>Copyright Â© 2024. Hatttrick. All Rights Reserved.</small>
      </footer>
    </div>
  );
}

export default Clubs;
