import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/leagues.css'; // Adjust if needed

import axios from 'axios';

const League = () => {
  const [standingsData, setStandingsData] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5000/standings/Premier%20League');
        setStandingsData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []); // Empty dependency array ensures useEffect runs once on component mount

  return (
    <div className="container my-4"> {/* Added Bootstrap classes for margin */}
      <div className="row justify-content-center">
        <div className="col-lg-10">
          <h1 className="text-center mb-4">Premier League Standings</h1>

          <TableComponent data={standingsData} /> {/* Pass fetched data as prop */}

          <footer className="mt-5">
            <small className="text-muted">Copyright Â© 2024. Hatttrick. All Rights Reserved.</small>
          </footer>
        </div>
      </div>
    </div>
  );
}

export default League;
