import React, { useEffect, useState } from 'react';
import '../styles/leagues.css';

function Leagues() {
  const [standings, setStandings] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('leagues.json')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        return response.json();
      })
      .then(data => {
        const premierLeagueStandings = data['Premier League'] ? data['Premier League'].standings : [];
        setStandings(premierLeagueStandings);
      })
      .catch(error => {
        setError(error);
        console.error('Error fetching data:', error);
      });
  }, []); // Empty dependency array ensures useEffect runs only once on component mount

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div className="container">
      <h1>Premier League Standings</h1>
      <table className="table" id="standingsTable">
        <thead>
          <tr>
            <th>Position</th>
            <th>Team</th>
            <th>Crest</th>
            <th>Points</th>
            <th>Games Won</th>
            <th>Games Lost</th>
            <th>Goals For</th>
            <th>Goals Against</th>
            <th>Goal Difference</th>
          </tr>
        </thead>
        <tbody>
          {standings.map((team, index) => (
            <tr key={index}>
              <td>{team.position}</td>
              <td>{team.team_name}</td>
              <td><img src={team.team_crest} alt={team.team_name} className="img-fluid rounded-end league-img" /></td>
              <td>{team.points}</td>
              <td>{team.games_won}</td>
              <td>{team.games_lost}</td>
              <td>{team.goals_for}</td>
              <td>{team.goals_against}</td>
              <td>{team.goal_difference}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <footer>
        <small>Copyright Â© 2024. Hatttrick. All Rights Reserved.</small>
      </footer>
    </div>
  );
}

export default Leagues;
