import React from 'react';
import '../styles/leagues.css';
import leaguesData from '../data/leagueinfo.json'; // Adjust the path as necessary

function Leagues() {
  return (
    <div className="container">
      <h1>Leagues</h1>
      {leaguesData.Leagues.map((league, index) => (
        <div key={index} className="card mb-3 league-card">
          <div className="row g-0">
            <div className="col-md-8">
              <div className="card-body">
                <h2>{league.League}</h2>
                <p><strong>Country:</strong> {league.Country}</p>
                <p><strong>Current Champion:</strong> {league['Current Champion']}</p>
                <p><strong>Top Scorer:</strong> {league['Top Scorer']}</p>
                <p><strong>Year Founded:</strong> {league['Year Founded']}</p>
                <p><strong>Games Per Season:</strong> {league['Games Per Season']}</p>
              </div>
            </div>
            <div className="col-md-4">
              <img src={`../Assets/${league.League.toLowerCase().replace(' ', '')}logo.png`} className="img-fluid rounded-end league-img" alt={league.League} />
            </div>
          </div>
        </div>
      ))}
      <footer>
        <small>Copyright Â© 2024. Hatttrick. All Rights Reserved.</small>
      </footer>
    </div>
  );
}

export default Leagues;
