import React, { useState, useEffect } from 'react';
import TableComponent from '../compenents/StandingsTable.js'; // Import your TableComponent if needed
import '../styles/leagues.css';

function Leagues() {
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    // Fetch data from local JSON file
    fetch('/Users/ang3l/Documents/Software Engineering/cs373-idb/backend/leagues.json')
      .then(response => response.json())
      .then(data => {
        const standings = data['Premier League'].standings;

        // Transform the object into an array of teams
        const teamsArray = Object.keys(standings).map(team => ({
          position: standings[team].position,
          name: team.replace(/-/g, ' '),
          crestUrl: standings[team].team_crest,
          points: standings[team].points,
          gamesWon: standings[team].games_won,
          gamesLost: standings[team].games_lost,
          goalsFor: standings[team].goals_for,
          goalsAgainst: standings[team].goals_against,
          goalDifference: standings[team].goal_difference,
        }));

        setTeams(teamsArray);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="container">
      <h1>League Standings</h1>
      <table className="table">
        <thead>
          <tr>
            <th>Position</th>
            <th>Team</th>
            <th>Crest</th>
            <th>Points</th>
            <th>Games Won</th>
            <th>Games Lost</th>
            <th>Goals For</th>
            <th>Goals Against</th>
            <th>Goal Difference</th>
          </tr>
        </thead>
        <tbody>
          {teams.map((team, index) => (
            <tr key={index}>
              <td>{team.position}</td>
              <td>{team.name}</td>
              <td><img src={team.crestUrl} alt={team.name} /></td>
              <td>{team.points}</td>
              <td>{team.gamesWon}</td>
              <td>{team.gamesLost}</td>
              <td>{team.goalsFor}</td>
              <td>{team.goalsAgainst}</td>
              <td>{team.goalDifference}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <footer>
        <small>
          Copyright Â© 2024. Hatttrick. All Rights Reserved.
        </small>
      </footer>
    </div>
  );
}

export default Leagues;
