import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Club = () => {
  const { name } = useParams();
  const [clubInfo, setClubInfo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClubInfo = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:5001/club/${}'); // Replace with your API URL
        setClubInfo(response.data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchClubInfo();
  }, [name]);

  const generateMapLink = (address) => {
    if (!address) return "";
    const encodedAddress = encodeURIComponent(address.trim());
    return `https://www.google.com/maps/search/?api=1&query=${encodedAddress}`;
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!clubInfo) {
    return <div>No club information available.</div>;
  }

  const mapUrl = generateMapLink(clubInfo.address);

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-lg-6">
          <h1 className="text-center mb-4">{clubInfo.name} Information</h1>
          <div className="card">
            <img src={clubInfo.crest} className="card-img-top" alt="Club Crest" />
            <div className="card-body">
              <h5 className="card-title">{clubInfo.name}</h5>
              <p className="card-text">
                <strong>Address:</strong> <a href={mapUrl} target="_blank" rel="noopener noreferrer">{clubInfo.address}</a>
              </p>
              <p className="card-text">
                <strong>Website:</strong> <a href={clubInfo.website} target="_blank" rel="noopener noreferrer">{clubInfo.website}</a>
              </p>
              <p className="card-text"><strong>Venue:</strong> {clubInfo.venue}</p>
              <p className="card-text"><strong>Founded:</strong> {clubInfo.founded}</p>
              <p className="card-text"><strong>Coach:</strong> {clubInfo.coach}</p>
              <p className="card-text"><strong>Area:</strong> {clubInfo.area}</p>
            </div>
          </div>
        </div>
      </div>
      <footer className="mt-5">
        <small className="text-muted">Copyright Â© 2024. Hatttrick. All Rights Reserved.</small>
      </footer>
    </div>
  );
};

export default Club;
