import React, { useState, useEffect } from 'react';
import '../styles/clubs.css';

function Clubs() {
  const [teamsData, setTeamsData] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const perPage = 12; // Number of teams per page

  useEffect(() => {
    fetch('http://127.0.0.1:5000/teams')
      .then(response => response.json())
      .then(data => {
        console.log('Fetched data:', data); // Debugging to see fetched data structure
        setTeamsData(data);
      })
      .catch(error => console.error('Error:', error));
  }, []);

  // Function to handle page change
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Check if teamsData is populated and structured correctly
  if (!Object.keys(teamsData).length) {
    return <div>Loading...</div>;
  }

  // Function to slice teams for pagination
  const sliceTeamsForPage = (league) => {
    const teams = teamsData[league].teams;
    const startIndex = (currentPage - 1) * perPage;
    return Object.keys(teams)
      .slice(startIndex, startIndex + perPage)
      .reduce((obj, key) => {
        obj[key] = teams[key];
        return obj;
      }, {});
  };

  // Paginated teams for current page
  const paginatedTeams = Object.keys(teamsData).reduce((acc, league) => {
    acc[league] = sliceTeamsForPage(league);
    return acc;
  }, {});

  return (
    <div className="clubs-container">
      <header className="header">
        <h1>Clubs Information</h1>
      </header>
      <div className="teams-grid">
        {Object.keys(paginatedTeams).map(league => (
          <React.Fragment key={league}>
            <h2>{league}</h2>
            <div className="teams-row">
              {Object.keys(paginatedTeams[league]).map(teamName => {
                const team = paginatedTeams[league][teamName];
                return (
                  <div className="team-card card" key={team.id}>
                    <img
                      src={team.crest}
                      className="card-img-top img-fluid"
                      alt={teamName}
                    />
                    <div className="card-body">
                      <h3 className="card-title">{teamName}</h3>
                      <p className="card-text">Coach: {team.coach}</p>
                      <p className="card-text">Founded: {team.founded}</p>
                      <p className="card-text">Venue: {team.venue}</p>
                      <a
                        href={team.website}
                        className="btn btn-primary"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        Website
                      </a>
                    </div>
                  </div>
                );
              })}
            </div>
          </React.Fragment>
        ))}
      </div>
      <div className="pagination">
        {Object.keys(paginatedTeams).map((league, index) => (
          <button key={index + 1} onClick={() => handlePageChange(index + 1)} className="page-link">
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Clubs;
