import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Teammates = ({ playerName }) => {
  const [teammates, setTeammates] = useState([]);

  useEffect(() => {
    const fetchTeammates = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:5000/teammates/${playerName}`);
        setTeammates(response.data);
      } catch (error) {
        console.error('Failed to fetch teammates:', error);
      }
    };

    if (playerName) {
      fetchTeammates();
    }
  }, [playerName]);

  if (!teammates.length) {
    return <div>Loading teammates...</div>;
  }

  return (
    <div className="teammates-container" style={{ display: 'flex', overflowX: 'auto', padding: '20px' }}>
      {teammates.map((teammate) => (
        <div key={teammate.id} style={{ flex: '0 0 auto', width: '200px', marginRight: '15px', textAlign: 'center' }}>
          <img src={teammate.photo} alt={teammate.name} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
          <div>
            <h4>{teammate.name}</h4>
            <p>{teammate.position}</p>
          </div>
        </div>
      ))}
    </div>
  );
};
