import React, { useState, useEffect } from 'react';
import TableComponent from './TableComponent';
import '../styles/leagues.css';

function Leagues() {
  const [teams, setTeams] = useState([]);

  useEffect(() => {
    // Simulating fetching data from an API
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => {
        // Assuming data is an array of objects with necessary fields
        const formattedData = data.map((user, index) => ({
          position: index + 1,
          name: user.name,
          crestUrl: `https://via.placeholder.com/30?text=${user.name}`, // Placeholder URL
          points: Math.floor(Math.random() * 50), // Example random points
          gamesWon: Math.floor(Math.random() * 20),
          gamesLost: Math.floor(Math.random() * 10),
          goalsFor: Math.floor(Math.random() * 50),
          goalsAgainst: Math.floor(Math.random() * 50),
          goalDifference: Math.floor(Math.random() * 50) - Math.floor(Math.random() * 50),
        }));
        setTeams(formattedData);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="container">
      <h1>League Standings</h1>
      <TableComponent data={teams} />
      <footer>
        <small>Copyright Â© 2024. Hatttrick. All Rights Reserved.</small>
      </footer>
    </div>
  );
}

export default Leagues;
