import React, { useState, useEffect } from 'react';
import '../styles/clubs.css';

function Clubs() {
  const [teamsData, setTeamsData] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const perPage = 12; // Number of teams per page

  useEffect(() => {
    fetch('http://127.0.0.1:5000/teams')
      .then(response => response.json())
      .then(data => {
        console.log('Fetched data:', data); // Debugging to see fetched data structure
        setTeamsData(data);
      })
      .catch(error => console.error('Error:', error));
  }, []);

  // Check if teamsData is populated and structured correctly
  if (!Object.keys(teamsData).length) {
    return <div>Loading...</div>;
  }

  const leagues = Object.keys(teamsData);

  const indexOfLastTeam = currentPage * perPage;
  const indexOfFirstTeam = indexOfLastTeam - perPage;

  const currentTeams = leagues.reduce((acc, league) => {
    if (teamsData[league].teams && Object.keys(teamsData[league].teams).length > 0) {
      acc[league] = Object.entries(teamsData[league].teams)
                    .slice(indexOfFirstTeam, indexOfLastTeam)
                    .reduce((obj, [teamName, team]) => {
                      obj[teamName] = team;
                      return obj;
                    }, {});
    } else {
      console.error(`Invalid data structure for league: ${league}`);
    }
    return acc;
  }, {});

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="clubs-container">
      <header className="header">
        <h1>Clubs Information</h1>
      </header>
      <div className="teams-grid">
        {leagues.map(league => (
          <React.Fragment key={league}>
            <h2>{league}</h2>
            <div className="teams-row">
              {Object.entries(currentTeams[league]).map(([teamName, team]) => (
                <div className="team-card card" key={team.id}>
                  <img
                    src={team.crest}
                    className="card-img-top img-fluid"
                    alt={teamName}
                  />
                  <div className="card-body">
                    <h3 className="card-title">{teamName}</h3>
                    <p className="card-text">Coach: {team.coach}</p>
                    <p className="card-text">Founded: {team.founded}</p>
                    <p className="card-text">Venue: {team.venue}</p>
                    <a
                      href={team.website}
                      className="btn btn-primary"
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Website
                    </a>
                  </div>
                </div>
              ))}
            </div>
          </React.Fragment>
        ))}
      </div>
      <div className="pagination">
        {Array.from({ length: Math.ceil(leagues.length / perPage) }, (_, index) => (
          <button key={index + 1} onClick={() => paginate(index + 1)} className="page-link">
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Clubs;
