import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // Ensure useParams is properly imported
import Teammates from '../compenents/teammates';

const PlayerInfo = () => {
  const [player, setPlayer] = useState(null);
  const { name } = useParams(); // This will get the 'name' URL parameter

  useEffect(() => {
    const fetchPlayerData = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:5000/players/${name}`); // Use your API endpoint
        setPlayer(response.data);
      } catch (error) {
        console.error('Error fetching player data:', error);
      }
    };

    fetchPlayerData();
  }, [name]); // Dependency array now includes 'name'

  if (!player) {
    return <div>Loading player data...</div>;
  }

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card text-center">
            <img
              src={player.photo}
              className="card-img-top mx-auto mt-4"
              style={{ width: '150px', height: '200px', objectFit: 'cover' }}
              alt={player.name}
            />
           <div className="card-body">
              <h5 className="card-title">{player.name}</h5>
              <ul className="list-group list-group-flush text-start">
                <li className="list-group-item"><strong>Age:</strong> {player.age}</li>
                <li className="list-group-item"><strong>Number:</strong> {player.number}</li>
                <li className="list-group-item"><strong>Position:</strong> {player.position}</li>
                <li className="list-group-item"><strong>Club:</strong> {player.club}</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
      <Teammates playerName={name} />

    </div>
  );
};

export default PlayerInfo;
