import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/leagues.css'; // Adjust if needed
import axios from 'axios';
import { useParams } from 'react-router-dom';

const League = () => {
  const { id } = useParams(); // Get the league name from the URL
  const [standingsData, setStandingsData] = useState({});
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:5000/standings/${id}`);
        const standings = response.data;
        console.log(response);

        // Check if standings and standings.standings exist
        if (standings && standings.standings) {
          // Convert standings object to array and sort by position
          const standingsArray = Object.entries(standings.standings).map(([teamName, teamData]) => ({
            ...teamData,
            name: teamName,
          }));

          const sortedStandingsArray = standingsArray.sort((a, b) => a.position - b.position);

          // Convert the sorted array back to an object
          const sortedStandings = sortedStandingsArray.reduce((acc, team) => {
            acc[team.name] = team;
            return acc;
          }, {});

          setStandingsData({ ...standings, standings: sortedStandings });
        } else {
          setError('Standings data is not available.');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
        setError('Error fetching data.');
      }
    };

    fetchData();
  }, [id]); // Dependency array includes id to refetch data when id changes

  return (
    <div className="container my-4"> {/* Added Bootstrap classes for margin */}
      <div className="row justify-content-center">
        <div className="col-lg-10">
          <h1 className="text-center mb-4">{id} Standings</h1> {/* Display dynamic league name */}
          {error ? (
            <div className="alert alert-danger" role="alert">
              {error}
            </div>
          ) : (
            <TableComponent data={standingsData.standings} /> /* Pass sorted data as prop */
          )}
          <footer className="mt-5">
            <small className="text-muted">Copyright Â© 2024. Hatttrick. All Rights Reserved.</small>
          </footer>
        </div>
      </div>
    </div>
  );
}

export default League;
