import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import '../styles/club.css';

const Club = () => {
  const { name } = useParams();
  const [clubInfo, setClubInfo] = useState(null);
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClubInfo = async () => {
      try {
        // Fetch club details
        const clubResponse = await axios.get(`http://127.0.0.1:5001/club/${name}`);
        setClubInfo(clubResponse.data[name]);

        // Fetching players for the club using the provided API endpoint
        const playersResponse = await axios.get(`http://127.0.0.1:5000/team/${name}`);
        setPlayers(playersResponse.data);
      } catch (err) {
        console.error("Error fetching data:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchClubInfo();
  }, [name]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!clubInfo) return <div>No club information available.</div>;

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-lg-6">
          <h1 className="text-center mb-4">{clubInfo.name} Information</h1>
          <div className="card">
            <img src={clubInfo.crest} className="card-img-top club-crest" alt="Club Crest" />
            <div className="card-body">
              <p><strong>Address:</strong> <a href={`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(clubInfo.address)}`} target="_blank" rel="noopener noreferrer">{clubInfo.address}</a></p>
              <p><strong>Website:</strong> <a href={clubInfo.website} target="_blank" rel="noopener noreferrer">{clubInfo.website}</a></p>
              <p><strong>Venue:</strong> {clubInfo.venue}</p>
              <p><strong>Founded:</strong> {clubInfo.founded}</p>
              <p><strong>Coach:</strong> {clubInfo.coach}</p>
              <p><strong>Area:</strong> {clubInfo.area}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Club;
