import React, { useState, useEffect } from 'react';
import '../styles/clubs.css';

function Clubs() {
  const [teamsData, setTeamsData] = useState({});
  const [currentPage, setCurrentPage] = useState(1);
  const [teamsPerPage] = useState(6); // Number of teams to display per page

  useEffect(() => {
    fetch('http://127.0.0.1:5000/teams')
      .then(response => response.json())
      .then(data => {
        console.log('Fetched data:', data); // Debugging to see fetched data structure
        setTeamsData(data);
      })
      .catch(error => console.error('Error:', error));
  }, []);

  // Logic to calculate which teams to display based on current page
  const indexOfLastTeam = currentPage * teamsPerPage;
  const indexOfFirstTeam = indexOfLastTeam - teamsPerPage;
  const currentTeams = Object.keys(teamsData)
    .map(league => teamsData[league].teams)
    .flat()
    .slice(indexOfFirstTeam, indexOfLastTeam);

  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <div className="clubs-container">
      <header className="header">
        <h1>League Information</h1>
        <p>All the info above</p>
      </header>
      <div className="teams-grid">
        {currentTeams.map(team => (
          <div className="team-card card" key={team.id}>
            <img
              src={team.crest}
              className="card-img-top img-fluid"
              alt={team.name} // Assuming team name is used as alt text
            />
            <div className="card-body">
              <h3 className="card-title">{team.name}</h3>
              <p className="card-text">Coach: {team.coach}</p>
              <p className="card-text">Founded: {team.founded}</p>
              <p className="card-text">Venue: {team.venue}</p>
              <a
                href={team.website}
                className="btn btn-primary"
                target="_blank"
                rel="noopener noreferrer"
              >
                Website
              </a>
            </div>
          </div>
        ))}
      </div>
      {/* Pagination */}
      <div className="pagination">
        <nav>
          <ul className="pagination">
            {[...Array(Math.ceil(Object.keys(teamsData).length / teamsPerPage)).keys()].map(pageNumber => (
              <li key={pageNumber + 1} className="page-item">
                <button onClick={() => paginate(pageNumber + 1)} className="page-link">
                  {pageNumber + 1}
                </button>
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </div>
  );
}

export default Clubs;
