import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/clubs.css';

const TEAMS_PER_PAGE = 12; // Number of teams per page

function Clubs() {
  const [teamsData, setTeamsData] = useState({});
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/teams')
      .then(response => response.json())
      .then(data => {
        console.log('Fetched data:', data); // Debugging to see fetched data structure
        setTeamsData(data);
      })
      .catch(error => console.error('Error:', error));
  }, []);

  // Function to handle page change
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Calculate total pages
  const totalLeagues = Object.keys(teamsData).length;
  const totalPages = Math.ceil(totalLeagues / TEAMS_PER_PAGE);

  // Calculate start and end index for current page
  const startIndex = (currentPage - 1) * TEAMS_PER_PAGE;
  const endIndex = Math.min(startIndex + TEAMS_PER_PAGE, totalLeagues);

  return (
    <div className="clubs-container">
      <header className="header">
        <h1>Clubs Information</h1>
      </header>
      <div className="teams-grid">
        {Object.keys(teamsData).slice(startIndex, endIndex).map(league => (
          <div key={league}>
            <h2>{league}</h2>
            <div className="teams-row">
              {Object.keys(teamsData[league].teams).map(teamName => {
                const team = teamsData[league].teams[teamName];
                return (
                  <div className="team-card card" key={team.id}>
                    <Link to={`/league/${team.id}`}>
                      <img
                        src={team.crest}
                        className="card-img-top img-fluid"
                        alt={teamName}
                      />
                    </Link>
                    <div className="card-body">
                      <h3 className="card-title">{teamName}</h3>
                      <p className="card-text">Coach: {team.coach}</p>
                      <p className="card-text">Founded: {team.founded}</p>
                      <p className="card-text">Venue: {team.venue}</p>
                      <a
                        href={team.website}
                        className="btn btn-primary"
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        Website
                      </a>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        ))}
      </div>
      <div className="pagination">
        {/* Previous Page Button */}
        <button
          className="btn btn-primary"
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        {/* Next Page Button */}
        <button
          className="btn btn-primary"
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
        >
          Next
        </button>
        <span>Page {currentPage} of {totalPages}</span>
      </div>
    </div>
  );
}

export default Clubs;
