import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../styles/club.css'; // Ensure the CSS is correctly linked
import Teammates from '../compenents/teammates';

const Club = () => {
  const { name } = useParams();
  const [clubInfo, setClubInfo] = useState(null);
  const [teammates, setTeammates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClubInfo = async () => {
      try {
        const clubResponse = await axios.get(`http://127.0.0.1:5001/club/${name}`);
        setClubInfo(clubResponse.data[name]);

        const teammatesResponse = await axios.get(`http://127.0.0.1:5000/team/${name}`);
        setTeammates(teammatesResponse.data);
      } catch (err) {
        console.error("Error fetching data:", err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchClubInfo();
  }, [name]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!clubInfo) return <div>No club information available.</div>;

  return (
    <div className="players-container mt-5"> {/* Adjusted class name for styling */}
      <div className="header text-center mb-4">
        <h1> Information</h1>
      </div>
      <div className="players-grid justify-content-center">
        <div className="player-card">
          <img src={clubInfo.crest} className="card-img-top" alt="Club Crest" style={{ width: '100%', objectFit: 'contain' }} />
          <div className="card-body">
            <ul className="list-group list-group-flush text-start">
              <li className="list-group-item">
                <strong>Address:</strong>
                <a href={`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(clubInfo.address)}`}
                   target="_blank" 
                   rel="noopener noreferrer">
                  {clubInfo.address}
                </a>
              </li>
              <li className="list-group-item">
                <strong>Website:</strong>
                <a href={/^https?:\/\//.test(clubInfo.website) ? clubInfo.website : `http://${clubInfo.website}`} 
                   target="_blank" 
                   rel="noopener noreferrer">
                  {clubInfo.website}
                </a>
              </li>
              <li className="list-group-item"><strong>Venue:</strong> {clubInfo.venue}</li>
              <li className="list-group-item"><strong>Founded:</strong> {clubInfo.founded}</li>
              <li className="list-group-item"><strong>Coach:</strong> {clubInfo.coach}</li>
              <li className="list-group-item"><strong>Area:</strong> {clubInfo.area}</li>
            </ul>
          </div>
        </div>
      </div>
      <div className="teammates-header text-center my-4">
        <h3>{name}'s Teammates</h3>
      </div>
      <Teammates teammates={teammates} />
    </div>
  );
};

export default Club;
